mapper.select:
SELECT USERID, TEAMID, PASSWORD, NAME, SSN, ROLL, AGE, GENDER, SUBJECT FROM MEMBER 

	
		USERID LIKE #{userId}
	
	
		AND PASSWORD LIKE #{password}

SELECT USERID, TEAMID, PASSWORD, NAME, SSN, ROLL, AGE, GENDER, SUBJECT FROM MEMBER 
WHERE USERID LIKE #{userId}
	AND PASSWORD LIKE #{password}

mapper.delete:
  	DELETE FROM MEMBER 
	WHERE USERID LIKE #{userId}
			AND PASSWORD LIKE #{password};

mapper.update:
  	UPDATE MEMBER
	SET ROLL = #{roll},PASSWORD = #{password},TEAMID = #{teamId}
	WHERE USERID LIKE #{userId};
